import React, { ReactElement, useState } from "react";
import { HealmeLogo } from "../../../constants/constants";
import { DocPic } from "../../../constants/constants";
import './_doc-select.css';
import Cookies from "js-cookie";

const DocSelect: React.FC = () => {
    const [doctors, setDoctors] = useState<ReactElement[]>([]);// Maintain an array of doctors

    const handleDoctorButtonClick = () => {
        window.location.href = '/confirm-appointment'; // Navigate to the desired page
    };

    // Function to add a doctor to the list
    const addDoctor = () => {
        const newDoctor = (
            <div key={doctors.length} className="doctor-confirm-panel">
                <div className="doc-photo">
                    <img className="doc" src={DocPic} alt="Pic of the doctor" />
                </div>
                <div className="appointment-description">
                    <div>Dr. Anita Priyamouli</div>
                    <div>Consultant - Dental Surgery BDS</div>
                </div>
                <button type="button" className="doctor-btn" onClick={handleDoctorButtonClick}>
                    Book An Appointment
                </button>
            </div>
        );

        setDoctors([...doctors, newDoctor]);
    };

    return (
        <div className="home-page-container">
            <div className="header-container">
                <div className="logo-container">
                    <img className="logo-img" src={HealmeLogo} alt="Logo" />
                </div>
            </div>
            <div>Listing {Cookies.get('doctype')}</div>
            <div className="body-container">
                <button onClick={addDoctor}>Add Doctor</button>

                {/* Display the list of doctors */}
                {doctors.map((doctor, index) => (
                    <div key={index}>{doctor}</div>
                ))}
            </div>
        </div>
    );
};

export default DocSelect;
